Template.register.onRendered(function () {
    $('.ui.form')
        .form({
            on: 'blur',
            fields: {
                name: {
                    identifier: 'name',
                    rules: [
                        {
                            type   : 'empty',
                            prompt : 'Please enter your username'
                        }
                    ]
                },
                email: {
                    identifier  : 'email',
                    rules: [

                        {
                            type   : 'empty,email',
                            prompt : 'Please enter a valid e-mail'
                        }
                    ]
                },

                password: {
                    identifier: 'password',
                    rules: [
                        {
                            type   : 'empty,minLength[6]',
                            prompt : 'Your password must be at least {ruleValue} characters'
                        }
                    ]
                },
                repassword: {
                    identifier: 'repassword',
                    rules: [
                        {
                            type   : 'empty,minLength[6]',
                            prompt : 'Your confirm password must be at least {ruleValue} characters'
                        }
                    ]
                },
            }
        })



});
